function [theta0, theta1] = regresionunivariable(ratioaprendizaje)  datos = load("data-p1/ex1data1.txt");  theta0 = 0;  theta1 = 0;  diffuncost = 1  m = rows(datos);    while diffuncost > 0.0001        #calculo funcion coste    sumatorio = sum((theta0 + (theta1*datos(:,1)) - datos(:,2)).^2);    funcostanterior = (1/(2*m))*sumatorio;      sumatoriotheta0 = sum((theta0 + (theta1*datos(:,1))) - datos(:,2));    sumatoriotheta1 = sum(((theta0 + (theta1*datos(:,1))) - datos(:,2)).*datos(:,1));        diftheta0 = (ratioaprendizaje/m) * sumatoriotheta0;    diftheta1 = (ratioaprendizaje/m) * sumatoriotheta1;        theta0 = theta0 - diftheta0;    theta1 = theta1 - diftheta1;        #calculo funcion coste    sumatorio = sum((theta0 + (theta1*datos(:,1)) - datos(:,2)).^2);    funcost = (1/(2*m))*sumatorio;        diffuncost = funcostanterior - funcost;      endwhile  endfunction